mapper.select:
SELECT BOARD_NUM , TITLE , MEM_ID , CREATE_DATE FROM board
WHERE ${searchType} LIKE CONCAT('%',#{searchValue},'%')
ORDER BY BOARD_NUM DESC;

mapper.insert:
INSERT INTO board (
    TITLE,
    CONTENT,
    MEM_ID
) VALUES (
    #{title},
    #{content},
    #{memId}
)

mapper.update:
UPDATE board
SET
    TITLE = #{title}
    , CONTENT = #{content}
WHERE BOARD_NUM = #{boardNum}
